curl -s "https://api.github.com/repos/tldraw/tldraw/git/trees/main?recursive=1"| jq -r '.tree[] | select(.path | test("(?i)context.md$")) | .path' | while read -r path; do echo "Downloading: $path"; echo -e "\n\n# ==========================================" >> tldraw_combined.md; echo "# FILE: $path" >> tldraw_combined.md; echo -e "# ==========================================\n" >> tldraw_combined.md; curl -s "https://raw.githubusercontent.com/tldraw/tldraw/main/$path" >> tldraw_combined.md; done
